try:    import simpleguiexcept ImportError:    import SimpleGUICS2Pygame.simpleguics2pygame as simplegui    simplegui.Frame._hide_status = Trueimport randomnum_range = 101comp_guess = random.randrange(1, num_range)count = 7count_store = 7current_game = None# template for "Guess the number" mini-project# input will come from buttons and an input field# all output for the game will be printed in the console# helper function to start and restart the gamedef new_game():    # initialize global variables used in your code here    print('Welcome to a new game of Guess, \nrange 0 to 100 unless selected otherwise')    comp_guess = random.randrange(1, num_range)    count = 7    count_store = count    frame.start()# define event handlers for control paneldef range100():    # button that changes the range to [0,100) and starts a new game    global comp_guess    global count    global current_game    global num_range    global count_store    num_range = 101    count = 17    count_store = count    comp_guess = random.randrange(1, 101)    new_game()def range1000():    # button that changes the range to [0,1000) and starts a new game    global comp_guess    global count    global num_range    global count_store    num_range = 1001    count = 10    count_store = count    comp_guess = random.randrange(1, 1001)    new_game()def input_guess(guess):    # main game logic goes here    global comp_guess, count, count_store    guess = int(guess)    if count == 0:        print("Tries used up... Restarting","count_store", count_store)        count = count_store        comp_guess = random.randrange(1, num_range)        range100()    if guess == comp_guess:        print("Correct! Well done!")        new_game()    elif guess > comp_guess:        print("Guess was " + str(guess))        print("Lower!")        print("Number of remaining guesses is " + str(count - 1))    elif guess < comp_guess:        print("Guess was " + str(guess))        print("Higher!")        print("Number of remaining guesses is " + str(count - 1))        print(comp_guess)    count -= 1# create frame# register event handlers for control elements and start frameframe = simplegui.create_frame("Guess the number", 200, 200)frame.add_button("Range is (0 to 100)",range100, 200)frame.add_button("Range is (0 to 1000)", range1000, 200)frame.add_input("Enter a guess", input_guess, 200)# call new_gamenew_game()# always remember to check your completed program against the grading rubric